import { Interceptor, KSBaseResponse } from '@locallife/base-request';

export interface RequestConfig {
    responseParseFunc?: (responseRaw: string) => KSBaseResponse;
    requestInterceptors?: Array<Interceptor>;
    responseInterceptors?: Array<Interceptor>;
    needLogin?: boolean; // 是否需要在登录态执行请求
    needResolveBigIntParam?: boolean; // 是否需要处理含 bigInt 类型的返回值
}

export const DefaultRequestConfig = {
    needResolveBigIntParam: false, // 默认不需要
};
