import {
    Interceptor,
    IRequestParam,
    KSBaseResponse,
    RequestConfig,
} from '@locallife/base-request';

export interface IRequest<T extends KSBaseResponse> {
    getDefaultConfig(): RequestConfig;

    initInnerInterceptors(
        requestInnerInterceptors: Array<Interceptor>,
        responseInnerInterceptors: Array<Interceptor>,
        config?: RequestConfig,
    );

    request(param: IRequestParam, config?: RequestConfig): Promise<T>;

    get(param: IRequestParam, config?: RequestConfig): Promise<T>;

    post(param: IRequestParam, config?: RequestConfig): Promise<T>;
}
