import Decimal from 'decimal.js-light';
// 数字转成decimal格式
export const getDecimal = price => {
  return new Decimal(price);
};

// 距离格式化
// 距离> 1km，展示实际距离x.x km，四舍五入，保留一位小数，若最后小数后最后一位为0，不显示，如99.0km，应展示为99km
// 距离在(0, 1km)内，展示 xxx m，保留整数，如 698m
export function getDistance(distance) {
  try {
    if (distance <= 0) {
      return '';
    }
    if (distance > 1000) {
      const distanceStr = new Decimal(distance).div(1000).toFixed(1);
      if (distanceStr[distanceStr.length - 1] === '0') {
        return distanceStr.substring(0, distanceStr.length - 2) + 'km';
      }
      return distanceStr + 'km';
    }
    return new Decimal(distance).toFixed(0) + 'm';
  } catch (e) {
    console.error(e);
    return '';
  }
}
