import { rem } from '@kid-ui/krn/lib/utils';

export const transAdaptScreenSize = (
    size?: number,
    adaptScreen: boolean = false,
    autoRatio: boolean = false,
    ratio: number = 1.0,
) => {
    if (size !== undefined) {
        if (autoRatio) {
            return size * ratio;
        }
        return adaptScreen ? rem(size) : size;
    }
    return undefined;
};
