import isString from 'lodash/isString';

// 判断数组非空
export function notEmptyArray(content?: any[]): content is any[] {
    return Array.isArray(content) && content?.length > 0;
}

// 判断对象为空
export function emptyObject(content?: any): content is null | undefined {
    return content === null || content === undefined;
}

function myIsString(content?: string): content is string {
    return isString(content);
}

// 判断字符串非空
export function stringNotEmpty(content?: string) {
    return myIsString(content) && content.length > 0;
}
