/**
 * 处理列表item被加载的逻辑，一般用于item展现的打点上报，只会上报一次
 *
 * @param idProvider    获取id的方法
 * @param func          执行的方法
 * @returns             列表item被加载的回调引用
 */
export default function useFeedMount<T>(idProvider: (item: T) => string, func: (feed: T, index: number) => void): (item: T, index: number) => void;
