export interface TextLayoutInfo {
    text?: string;
    hasNextLine: boolean;
    firstLineBrand: boolean;
    firstLineText?: string;
    nextLineText?: string;
}
/**
 * 监听文本布局
 *
 * @param textStr 文本字符串
 * @returns [textInfo, onTextLayout] [文本信息, 用来监听文本布局的变化]
 */
export default function useOnTextLayout(textStr: string): [TextLayoutInfo, (e: any) => void];
