import { NativeModules } from 'react-native';

export const REQUEST_PATH = 'LOCALLIFE_KRN_REQUEST_PATH';
export const REQUEST_START = 'LOCALLIFE_KRN_REQUEST_START';
export const REQUEST_SUCCESS = 'LOCALLIFE_KRN_REQUEST_SUCCESS';
export const REQUEST_FAILED = 'LOCALLIFE_KRN_REQUEST_FAILED';

export class RequestRubas {
    static publish(event: string, payload?: number | string | object) {
        NativeModules.Kds.invoke(
            'tool',
            'publishRubas',
            JSON.stringify({
                event: event,
                payload: payload,
            }),
        );
    }

    static setDimension(dimensionKey: string, value: string) {
        NativeModules.Kds.invoke(
            'tool',
            'setRubasDimension',
            JSON.stringify({
                key: dimensionKey,
                value: value,
            }),
        );
    }
}
