import LocalLifeInitLogger from './LocalLifeInitLogger';

export default class LocalLifeCustomLogger {
    static async logCustomEvent(key: string, value: object) {
        try {
            let weblog = LocalLifeInitLogger.getWeblog();
            weblog?.sendImmediately('CUSTOM_EVENT', {
                key,
                value,
                biz: 'POI',
            });
        } catch (e) {
            console.error(`LocalLifeCustomLogger error:${e || ''}`);
        }
    }

    static async logCustomStatEvent(key: string, value: object) {
        try {
            let weblog = LocalLifeInitLogger.getWeblog();
            weblog?.sendImmediately('CUSTOM', {
                key,
                value,
                biz: 'POI',
            });
        } catch (e) {
            console.error(`LocalLifeCustomLogger error:${e || ''}`);
        }
    }
}
