export interface PVEventOptions {
    page: string;
    params: {
        [key: string]: any;
    };
    coPage?: boolean;
}

interface Params {
    name: string;
    error: Error | string;
}

export default class LoggerError extends Error {
    constructor({ name = '@locallife/log-js-error', error }: Params) {
        super();
        this.name = name;
        this.message = typeof error === 'string' ? error : error.message;
    }
}
