import React from 'react';
import { View, StyleSheet } from 'react-native';
import { KidNetError, useKidColor } from '@kid-ui/krn';

export type TErrorProps = {
    onRetry?: () => void;
};

const styles = StyleSheet.create({
    errorContainer: {
        position: 'absolute',
        width: '100%',
        height: '100%',
        justifyContent: 'center',
        alignItems: 'center',
        flex: 1,
    },
});

// 错误页面
const ErrorScene = (props: TErrorProps) => {
    const bgColor = useKidColor('cs_common_background_secondary');
    return (
        <View style={[styles.errorContainer, { backgroundColor: bgColor }]}>
            <KidNetError
                onPress={props.onRetry}
                kid="style_emptystate_default"
                emptyStyle=""
            />
        </View>
    );
};

export default ErrorScene;
