import React from 'react';
import { View, StyleSheet } from 'react-native';
import { KidLoading, LoadingType, useKidColor } from '@kid-ui/krn';

const styles = StyleSheet.create({
    loading: {
        flex: 1,
        position: 'absolute',
        width: '100%',
        height: '100%',
        backgroundColor: 'black',
        justifyContent: 'center',
        alignItems: 'center',
    },
});
// loading页面
const LoadingScene = () => {
    const bgColor = useKidColor('cs_common_background_secondary');
    return (
        <View style={[styles.loading, { backgroundColor: bgColor }]}>
            <KidLoading type={LoadingType.gray} />
        </View>
    );
};

export default LoadingScene;
