import { Platform } from 'react-native';

export function getErrorCode(error: any) {
    if (error && typeof error === 'object') {
        if (error?.data?.code) {
            return error?.data?.code;
        }
        if (error?.code) {
            return error?.code;
        }
        if (error?.data?.status) {
            return error?.data?.status;
        }
        if (error?.status) {
            return error?.status;
        }
        return -1000;
    }
    return -10000;
}

export function getErrorMessage(error: any) {
    if (error && typeof error === 'object') {
        if (error?.data?.message) {
            return error?.data?.message;
        }
        if (error?.message) {
            return error?.message;
        }
        if (error?.data?.errMsg) {
            return error?.data?.errMsg;
        }
        if (error?.errMsg) {
            return error?.errMsg;
        }
        return 'error message parsing exception';
    }
    return 'error is nil';
}

export function getRubasTime(timestamp: number) {
    if (Platform.OS === 'ios') {
        return { value: timestamp };
    } else {
        return timestamp;
    }
}
