/**
 * 将输入的时间戳转换为对应的 「月日」
 * @param timeStamp 输入的时间戳
 * @returns 返回时间戳对应的年月日 如：1月4日
 */
export function getMonthDay(timeStamp) {
  if (timeStamp) {
    var time = new Date(timeStamp);
    var M = time.getMonth() + 1;
    var d = time.getDate();
    return M + '月' + d + '日';
  } else {
    return '';
  }
}
