interface _IAppInitProps {
    rootTag: number;
    onCreateTimestamp: number;
    BundleVersionCode: string;
    bundleId: string;
    componentName: string;
}

export const utilsGlobalConfig: Partial<_IAppInitProps> = {};

export const setUtilsGlobalConfig = (
    nextGlobalConfig: Partial<_IAppInitProps>,
): void => {
    Object.assign(utilsGlobalConfig, nextGlobalConfig);
};
