/**
 * json反序列化
 * @param json json字符串
 * @param error 报错处理
 */
export function jsonParse(json, error?: (err: Error | unknown) => {}) {
    if (typeof json === 'string') {
        try {
            return JSON.parse(json);
        } catch (e) {
            console.log('jsonParse error', e);
            error && error(e);
            return null;
        }
    }
    return json;
}
