import { NativeModules } from 'react-native';

/**
 * 上报异常：dev环境下打印console，正式环境下上报埋点
 * @param tag 异常信息tag
 * @param message 异常具体信息
 */
export function logException(tag: string, message?: string) {
    if (__DEV__) {
        console.error(`Locallife-Util-${tag}=>${message}`);
    } else {
        NativeModules.KRNBasic.log(`Locallife-Util-${tag}=>${message}`);
    }
}
