import { NativeModules } from 'react-native';

/**
 * 将数字转换为带有单位的字符串
 * @param content 输入的数字
 * @returns 转换后的字符串
 */
export function numberWithUnit(content: number): string {
    let stringValue: string;
    if (NativeModules.KSLanguage.getLanguage() === 'en') {
        if (content >= 1000000000) {
            //1billion
            stringValue = `${(content / 1000000000).toFixed(1)}b`;
        } else if (content >= 1000000) {
            //1million
            stringValue = `${(content / 1000000).toFixed(1)}m`;
        } else if (content >= 1000) {
            //1k
            stringValue = `${(content / 1000).toFixed(1)}k`;
        } else {
            stringValue = `${content}`;
        }
    } else {
        if (content >= 100000000) {
            //大于1个亿
            stringValue = `${(content / 100000000).toFixed(1)}亿`; //只有简体中文, 所以这个亿字写死在代码里了. 不需要国际化key
        } else if (content >= 10000) {
            //大于1万
            stringValue = `${(content / 10000).toFixed(1)}w`;
        } else {
            stringValue = `${content}`;
        }
    }
    return stringValue;
}
