import { Platform } from 'react-native';
import { isIOSStatusBar59 } from './isIOSStatueBar59';

/**
 * 判断是否是灵动岛
 * @returns 是/否
 */
export function isDynamicIsland() {
    return (
        Platform.OS === 'ios' &&
        !Platform.isPad &&
        !Platform.isTVOS &&
        isIOSStatusBar59()
    );
}
