import { NativeModules } from 'react-native';

/**
 * 弹出toast
 * @param text toast对应文案
 */
export function showToast(
    text: string,
    type: 'error' | 'success' | 'normal' = 'normal',
    isAddToWindow: boolean = true,
) {
    NativeModules.Kds.invoke(
        'ui',
        'showToast',
        JSON.stringify({ type: type, text, isAddToWindow: isAddToWindow }),
    ).then(() => {});
}
