/**
 * 处理多少人想去（热度） 低于50人不展示，没有该字段不展示。
 * i. 数字大于4位数，展示x万，保留1位小数，小数点后是0则不显示小数点。如显示6万，6.1万
 * @param peopleNum {number} 个数
 * @return {number}
 */ 
export default (peopleNum: number) => {
    try {
        if (peopleNum < 50) {
            return '';
        }
        if (peopleNum >= 10000) {
            const fixNum: string = (peopleNum / 10000).toFixed(1);
            return `${Number(fixNum) / 1}万`;
        }
        return peopleNum;
    } catch (err) {
        return peopleNum;
    }
};
