declare const timeCycle: readonly ["s", "m", "h", "D", "M", "Y"];
declare type TimeCycle = typeof timeCycle[number];
interface Options {
    cycle?: TimeCycle;
}
/**
 * 倒计时格式化
 * @param timestamp 剩余毫秒时间戳
 * @returns
 */
export default function deadline(timestamp: number, options?: Options): {
    /**
     * 获取解析后的年月日时分秒数据
     * @returns
     */
    parse: () => {
        years: number;
        months: number;
        days: number;
        hours: number;
        minutes: number;
        seconds: number;
        millseconds: number;
        monthsPad: string;
        daysPad: string;
        hoursPad: string;
        minutesPad: string;
        secondsPad: string;
    };
    /**
     * 格式化
     * @param pattern 模板
     * @returns
     */
    format: (pattern?: string) => string;
};
export {};
