export class RestoreEnvPlugin {
  constructor(private config: {
    [name: string]: string;
  }) {}
  apply(compiler) {
      compiler.hooks.done.tapAsync(
          'RestoreEnvPlugin',
          (stats, callback) => {
              const envs = this.config || {}
              for (const name in envs) {
                const value = envs[name]
                if (value != null) {
                  process.env[name] = envs[name]
                }
              }
              callback();
          },
      );
  }
}