import type * as webpack from 'webpack';
import { getNormalizedWebpackOptions } from 'webpack/lib/config/normalization';

export const getNormalizedEntry = (config: webpack.Configuration): string[] => {
  return getNormalizedWebpackOptions(config).entry;
};

export const getEntryPoints = (config: webpack.Configuration): string[] => {
  const normalizedEntry = getNormalizedEntry(config);
  return Object.keys(normalizedEntry);
};