import { getYoda } from '../instance';

export async function checkCanUse(
    namespace: string,
    name: string
): Promise<boolean> {
    const yoda = getYoda();

    const checkReuslt = await yoda.bridge('tool', 'canIUse', {
        namespace,
        name,
    });

    return checkReuslt.result == '1' && checkReuslt.canUse;
}
