import { getUUId } from './uuid';

const markStr = 'event_';

/**
 * 获取内部调用Event传递id
 * @returns id 字符串
 */
export function getEventId(dicData?: any): string {
    return markStr + getUUId(dicData);
}

/**
 * 获取纯数字的id
 * @param idStr event id 字符串
 * @returns 纯数字字符
 */
export function getEventIdOnlyNumber(idStr: string) {
    return idStr.replace(markStr, '');
}

/**
 * 获取添加了事件前缀的id
 * @param idNumber number id
 * @returns 完整id
 */
export function getEventIdByNumber(idNumber: number) {
    return markStr + String(idNumber);
}

/**
 * 判断是否是Event的id
 * @param idStr 当前掉用id
 * @returns
 */
export function isEventId(callbackId: string) {
    return callbackId.startsWith(markStr);
}
