import { getUUId } from './uuid';

const markStr = 'too_performance_';

/**
 * 获取内部调用performance传递id
 * @returns id 字符串
 */
export function getPerformanceId(): string {
    return markStr + getUUId();
}

/**
 * 判断是否是performance的id
 * @param idStr 当前掉用id
 * @returns
 */
export function isPerformanceId(callbackId: string) {
    return callbackId.startsWith(markStr);
}
