#import "KDSReactCoreSwitchConfigCenter.h"

static BOOL kdsBackgroundImageEnabled = NO;
static BOOL kdsFixedEnabled = NO;
static BOOL kdsSdkLaunchTimeReportEnabled = NO;

@implementation KDSReactCoreSwitchConfigCenter

BOOL RCTKDSBackgroundImageEnabled(void)
{
    return kdsBackgroundImageEnabled;
}

void RCTKDSEnableBackgroundImage(BOOL enabled) {
    kdsBackgroundImageEnabled = enabled;
}

BOOL RCTKDSFixedEnabled(void)
{
    return kdsFixedEnabled;
}

void RCTKDSEnableFixed(BOOL enabled) {
    kdsFixedEnabled = enabled;
}

BOOL RCTKDSSdkLaunchTimeReportdEnabled(void)
{
    return kdsSdkLaunchTimeReportEnabled;
}

void RCTKDSEnableSdkLaunchTimeReportd(BOOL enabled) {
    kdsSdkLaunchTimeReportEnabled = enabled;
}


@end
