#import "KDSScrollViewManager.h"
#import "KDSScrollView.h"

@implementation KDSScrollViewManager

RCT_EXPORT_MODULE(KDSScrollView)

- (UIView *)view
{
  return [[KDSScrollView alloc] initWithEventDispatcher:self.bridge.eventDispatcher];
}

RCT_CUSTOM_VIEW_PROPERTY(backgroundImage, NSArray<NSDictionary *>, KDSScrollView)
{
    view.backgroundImage = json ? [RCTConvert NSArray:json] : nil;
}

@end
