#import "KDSViewManager.h"
#import "KDSView.h"
#import "RCTUIManagerUtils.h"

@implementation KDSViewManager

RCT_EXPORT_MODULE(KDSView)

- (dispatch_queue_t)methodQueue
{
  return RCTGetUIManagerQueue();
}

- (UIView *)view
{
#if TARGET_OS_TV
  return [RCTTVView new];
#else
  return [KDSView new];
#endif
}

RCT_CUSTOM_VIEW_PROPERTY(backgroundImage, NSArray<NSDictionary *>, KDSView)
{
    view.backgroundImage = json ? [RCTConvert NSArray:json] : nil;
}

@end
