package com.facebook.react.module.model;

import com.facebook.proguard.annotations.DoNotStrip;

@DoNotStrip
public class MethodDescriptor {
  @DoNotStrip
  public int argCount;
  @DoNotStrip
  public String name;
  @DoNotStrip
  public MethodValueKind retKind;
  @DoNotStrip
  public String signature;

  public MethodDescriptor() {}

  public MethodDescriptor(String name, int argCount, int retKind, String signature) {
    this.name = name;
    this.argCount = argCount;
    this.retKind = MethodValueKind.values()[retKind];
    this.signature = signature;
  }

  public String id() {
    return name + ":" + signature + ":" + retKind.name();
  }

  public enum MethodValueKind {
    VoidKind,
    BooleanKind,
    NumberKind,
    StringKind,
    ObjectKind,
    ArrayKind,
    FunctionKind,
    PromiseKind,
  }
}
