package com.facebook.react.transientpage;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;

import com.facebook.react.bridge.JSBundleLoaderDelegate;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactModuleWithSpec;
import com.facebook.react.module.annotations.ReactModule;
import com.facebook.react.turbomodule.core.interfaces.TurboModule;
import com.facebook.react.uimanager.ReactRoot;

@ReactModule(name = TransientBundleLoaderSpec.NAME)
public abstract class TransientBundleLoaderSpec extends ReactContextBaseJavaModule
    implements ReactModuleWithSpec, TurboModule {
  public static final String NAME = "TransientBundleLoader";

  public TransientBundleLoaderSpec(ReactApplicationContext reactApplicationContext) {
    super(reactApplicationContext);
  }

  public abstract boolean loadFirstPage(@Nullable JSBundleLoaderDelegate delegate, boolean actualLoad);
  public abstract boolean shouldAttachFirstPage(ReactContext reactContext, int rootTag);
  public abstract void notifyFirstPageAttached(ReactContext reactContext, ReactRoot reactRoot);

  @NonNull
  @Override
  public String getName() {
    return NAME;
  }
}
