package com.facebook.react.transientpage;

import com.facebook.react.bridge.ReactContext;
import com.facebook.react.config.ReactFeatureFlags;

public class TransientUtils {
  public static TransientBundleLoaderSpec getBundleLoader(ReactContext reactContext) {
    if (!ReactFeatureFlags.useTurboModules) {
      return null;
    }
    TransientBundleLoaderSpec transientBundleLoaderSpec = null;
    try {
      transientBundleLoaderSpec = (TransientBundleLoaderSpec) reactContext.getCatalystInstance().getNativeModule(TransientBundleLoaderSpec.NAME);
    } catch (AssertionError ignored) {}
    return transientBundleLoaderSpec;
  }

  public static TransientEventEmitter getEventEmitter(ReactContext reactContext) {
    return reactContext.getJSModule(TransientEventEmitter.class);
  }
}
