#include <memory>
#include <string>
#include <unordered_map>

#include <ReactCommon/TurboModuleManagerDelegate.h>
#include <fbjni/fbjni.h>

namespace facebook {
namespace react {

class JavaTurboModuleManagerDelegate : public jni::HybridClass<JavaTurboModuleManagerDelegate, TurboModuleManagerDelegate> {
public:
  struct JavaTurboMethodInfo {
    size_t argCount;
    std::string name;
    int retKind;
    std::string signature;
  };

  static constexpr auto kJavaDescriptor =
      "Lcom/facebook/react/turbomodule/core/JavaTurboModuleManagerDelegate;";

  static jni::local_ref<jhybriddata> initHybrid(jni::alias_ref<jhybridobject>);

  static void registerNatives();

  std::shared_ptr<TurboModule> getTurboModule(const std::string name, const std::shared_ptr<CallInvoker> jsInvoker) override;
  std::shared_ptr<TurboModule> getTurboModule(const std::string name, const JavaTurboModule::InitParams &params) override;

  /**
   * Test-only method. Allows user to verify whether a TurboModule can be created
   * by instances of this class.
   */
  bool canCreateTurboModule(std::string name);
  void registerModules(const std::string &moduleName, int argCount, const std::string &funcName, int kind, const std::string &sign);

private:
  friend HybridBase;
  using HybridBase::HybridBase;

  std::unordered_map<std::string, std::vector<JavaTurboMethodInfo>> infoMap_;
};

} // namespace react
} // namespace facebook
