package com.facebook.react.views.deractors;

import androidx.annotation.Nullable;

import com.facebook.react.bridge.ReactContext;
import com.facebook.react.views.scroll.FpsListener;
import com.facebook.react.views.scroll.ReactScrollView;

public class KdsScrollView extends ReactScrollView {

  private BackgroundDecorViewManager mBackgroundDecorViewManager = null;

  public KdsScrollView(ReactContext context) {
    super(context);
    mBackgroundDecorViewManager = new BackgroundDecorViewManager();
  }

  public KdsScrollView(ReactContext context, @Nullable FpsListener fpsListener) {
    super(context, fpsListener);
    mBackgroundDecorViewManager = new BackgroundDecorViewManager();
  }

  public BackgroundDecorViewManager getBackgroundDecorViewManager() {
    return mBackgroundDecorViewManager;
  }
}
