package com.facebook.react.views.deractors;

import android.content.Context;

import com.facebook.react.views.view.ReactViewGroup;

public class KdsViewGroup extends ReactViewGroup {
  private BackgroundDecorViewManager mBackgroundDecorViewManager = null;
  public KdsViewGroup(Context context) {
    super(context);
    mBackgroundDecorViewManager = new BackgroundDecorViewManager();
  }

  public BackgroundDecorViewManager getBackgroundDecorViewManager() {
    return mBackgroundDecorViewManager;
  }
}
