package com.facebook.v8.liteexecutor;

import java.util.TimeZone;

import com.facebook.react.bridge.DebuggableJavaScriptExecutor;
import com.facebook.react.bridge.JavaScriptExecutor;
import com.facebook.react.bridge.JavaScriptExecutorFactory;

public class V8LiteExecutorFactory
  implements JavaScriptExecutorFactory, DebuggableJavaScriptExecutor {

  private final String mTraceFilePath;
  private final boolean mEnableStartupTrace;
  private boolean mIsSampling;

  public V8LiteExecutorFactory() {
    mTraceFilePath = "";
    mEnableStartupTrace = false;
    mIsSampling = false;
  }

  public V8LiteExecutorFactory(final String traceFilePath, boolean enableStartupTrace) {
    mTraceFilePath = traceFilePath;
    mEnableStartupTrace = enableStartupTrace;
    if (mEnableStartupTrace) {
      mIsSampling = true;
    }
  }

  @Override
  public JavaScriptExecutor create() {
    return new V8LiteExecutor(getTimezoneId(), mTraceFilePath, mEnableStartupTrace);
  }

  @Override
  public void startSamplingProfiler() {
    V8LiteExecutor.startTracing("");
    mIsSampling = true;
  }

  @Override
  public void stopSamplingProfiler(String filename) {
    V8LiteExecutor.stopTracing();
    mIsSampling = false;
  }

  @Override
  public boolean getIsSampling() {
    return mIsSampling;
  }

  @Override
  public String getTraceFilePath() {
    return mTraceFilePath;
  }

  @Override
  public String toString() {
    return "JSIExecutor+V8LiteRuntime";
  }

  private String getTimezoneId() {
    return TimeZone.getDefault().getID();
  }
}
