package com.facebook.v8.reactexecutor;

public class V8TraceCategory {

  private static final String DISABLE_BY_DEFAULT = "disabled-by-default-";

  public static final String CATEGORY_V8 = "v8";
  public static final String CATEGORY_COMPILE = DISABLE_BY_DEFAULT + "v8.compile";
  public static final String CATEGORY_RUNTIME = DISABLE_BY_DEFAULT + "v8.runtime";
  public static final String CATEGORY_CPU_PROFILER = DISABLE_BY_DEFAULT + "v8.cpu_profiler";
  public static final String CATEGORY_RUNTIME_STATS = DISABLE_BY_DEFAULT + "v8.runtime_stats";

  public static String buildDefaultCategories() {
    return buildCategories(CATEGORY_V8, CATEGORY_CPU_PROFILER, CATEGORY_COMPILE);
  }

  public static String buildCategories(String... args) {
    StringBuilder stringBuilder = new StringBuilder();
    for (String arg : args) {
      stringBuilder.append(arg).append(";");
    }
    return stringBuilder.toString();
  }
}
