#include "V8RuntimeFactory.h"

#include "V8Runtime.h"

namespace facebook {

std::unique_ptr<jsi::Runtime> createV8Runtime(
    const std::string &timezoneId,
    std::shared_ptr<react::ExecutorDelegate> delegate,
    const std::string &traceFilename,
    bool enableStartupTrace) {
  return std::make_unique<V8Runtime>(timezoneId, delegate, traceFilename, enableStartupTrace);
}

} // namespace facebook
